    private void btnCalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcActionPerformed
        // TODO add your handling code here:
        int n = Integer.parseInt( txtVal.getValue().toString());
        float a = n % 2;
        float b =(float) Math.pow(n, 3);
        float c = (float)Math.sqrt(n);
        float d = (float) Math.cbrt(n);
        float e = (float)Math.abs(n);
        lblRes.setText(Float.toString(a));
        lblCub.setText(Float.toString(b));
        lblRaq.setText(String.format("%.2f",c));
        lblRac.setText(String.format("%.2f",d));
        lblValo.setText(Float.toString(e));
    }//GEN-LAST:event_btnCalcActionPerformed

